import React, { useState } from 'react';
import "./App.css";
import Video from './Video'; 
import AddVideo from './AddVideo';

const videosData = [
  [
    {
      "id": 523523,
      "title": "Never Gonna Give You Up",
      "url": "https://www.youtube.com/watch?v=dQw4w9WgXcQ",
      "rating": 23
    },
