import React from 'react';



 // Initialize votes with the video's rating




function Video({ video, onRemove }) {
  const [votes, setVotes] = useState(video.rating);

  const handleUpVote = () => {
    setVotes(votes + 1); // Increase the votes by 1
  };

  const handleDownVote = () => {
    setVotes(votes - 1); // Decrease the votes by 1
  };

  return (
    <div className="Video-container">

      <h2>{video.title}</h2>

      <div className="video-embed">

        <iframe
          width="100%"
          height="315"
          // src={`https://www.youtube.com/embed/${new URL(video.url).searchParams.get('v')}`}
          title={video.title}
          allowFullScreen
        />
      </div>


      <p>Rating: {video.rating}</p>


      <button className="btn" onClick={() => onRemove(video.id)}>
        Remove Video
      </button>
      <button className="btn" onClick={handleUpVote}>
        Up Vote
      </button>

      {/* "Down Vote" button */}
      <button className="btn" onClick={handleDownVote}>
        Down Vote
      </button>
    </div>
  );
}

export default Video;
